import React, { Component } from "react";
import axios from "axios";
import {Form, Button} from 'reactstrap';
import {Modal, ModalHeader, ModalBody} from 'reactstrap';

class EditTask extends Component {
    constructor(props) {
        super(props)

        this.state = {
            title: '',
            body: '',
            completed: false,
            isModalOpen: false
        }

        this.toggleModal = this.toggleModal.bind(this);
    }

    toggleModal() {
        this.setState({
          isModalOpen: !this.state.isModalOpen
        });
    }

    render() {
        return(
            <div>

                    <button className="form-group btn btn-dark" toggle={this.toggleModal}>
                        Edit Task
                    </button>

                <Modal isOpen={this.state.isModalOpen} toggle={this.toggleModal} closeTimeoutMS={500}>
                    <ModalHeader toggle={this.toggleModal}>Add New Task</ModalHeader>
                    <ModalBody>
                        Hello
                    </ModalBody>
                </Modal>
            </div>
        )
    }
}

export default EditTask;
