import React, { Component } from "react";
import axios from "axios";
import {Form, Button} from 'reactstrap';
import {Modal, ModalHeader, ModalBody} from 'reactstrap';
import {EditForm} from './EditFormComponent';

class EditTask extends Component {
    constructor(props) {
        super(props)

        this.state = {
            isModalOpen: false
        }

        this.toggleModal = this.toggleModal.bind(this);
    }

    toggleModal() {
        this.setState({
          isModalOpen: !this.state.isModalOpen
        });
    }

    render() {
        console.log(this.props)
        return(
            <div>
                <button className="form-group btn btn-dark" onClick={this.toggleModal}>
                    Edit Task
                </button>

                <Modal isOpen={this.state.isModalOpen} toggle={this.toggleModal} closeTimeoutMS={500}>
                    <ModalHeader toggle={this.toggleModal}>Edit Task</ModalHeader>
                    <ModalBody>
                        <Form onSubmit={this.handleSubmit}>
                            <FormGroup row>
                                <Label htmlFor="title" md={3}>Title</Label>
                                <Col md={10}>
                                    <Input type="text" id="title" name="title"
                                    placeholder={this.props.title}
                                    onBlur={this.handleBlur('title')}
                                    onChange={this.handleInputChange}
                                    invalid={errors.title !== ''}/> 
                                </Col>
                            </FormGroup>
                <FormGroup row>
                    <Label htmlFor="body" md={3}>Description</Label>
                    <Col md={10}>
                        <Input type="textarea" id="body" name="body"
                        value={this.state.body}
                        onChange={this.handleInputChange} /> 
                    </Col>
                </FormGroup>
                <FormGroup row>
                    <Col md={{size: 10}}>
                        <Button type="submit" color="dark">
                            Add Task
                        </Button>
                    </Col>
                </FormGroup>
            </Form>
                    </ModalBody>
                </Modal>
            </div>
        )
    }
}

export default EditTask;
