import React, { Component } from "react";
import axios from "axios";
import {Modal, ModalHeader, ModalBody} from 'reactstrap';
import {Button, Form, FormGroup, Label, Input, Col} from 'reactstrap';

class EditTask extends Component {
    constructor(props) {
        super(props)

        this.state = {
            title: '',
            body: '',
            timestamp: this.props.timestamp,
            completed: false,
            isModalOpen: false
        }

        this.toggleModal = this.toggleModal.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleInputChange = this.handleInputChange.bind(this);
    }

    toggleModal() {
        this.setState({
          isModalOpen: !this.state.isModalOpen
        });
    }

    handleInputChange(event) {
        const target = event.target;
        const value = target.type === 'checkbox' ? target.checked : target.value;
        const name = target.name;

        this.setState({
          [name]: value
        });

    }

    handleSubmit(event) {
        alert("Current State is: " + JSON.stringify(this.state));
        event.preventDefault();
    }

    render() {
        console.log(this.props.editTask.id)
        return(
            <div>
                <button className="form-group btn btn-dark" onClick={this.toggleModal}>
                    Edit Task
                </button>

                <Modal isOpen={this.state.isModalOpen} toggle={this.toggleModal} closeTimeoutMS={500}>
                    <ModalHeader toggle={this.toggleModal}>Edit Task</ModalHeader>
                    <ModalBody>
                        <Form onSubmit={this.handleSubmit}>
                            <FormGroup row>
                                <Label htmlFor="title" md={3}>Title</Label>
                                <Col md={10}>
                                    <Input type="text" id="title" name="title"
                                    value={this.state.title}
                                    placeholder={this.props.toEdit}
                                    onChange={this.handleInputChange}/> 
                                </Col>
                            </FormGroup>
                            <FormGroup row>
                                <Label htmlFor="body" md={3}>Description</Label>
                                <Col md={10}>
                                    <Input type="text" id="body" name="body"
                                    value={this.state.body}
                                    placeholder={this.props.body}
                                    onChange={this.handleInputChange}/> 
                                </Col>
                            </FormGroup>
                            <FormGroup row>
                                <Col md={{offset: 0}}>
                                <FormGroup check>
                                    <Label check>
                                        <Input type="checkbox"
                                            name="completed"
                                            checked={this.state.completed}
                                            onChange={this.handleInputChange} /> 
                                            <p>Completed</p>
                                    </Label>
                                </FormGroup>
                                </Col>
                            </FormGroup>
                            <FormGroup row>
                                <Col md={{size: 10}}>
                                    <Button type="submit" color="dark">
                                        Submit Changes
                                    </Button>
                                </Col>
                            </FormGroup>
                        </Form>
                    </ModalBody>
                </Modal>
            </div>
        )
    }
}

export default EditTask;
