import React, { Component } from "react";
import axios from "axios";


class Main extends Component {
    constructor(props) {
        super(props);

        this.state = {
            tasks: []
        }
    }

    createTaskList = () => {
        axios
            .get("/api/tasks/")
            .then((res) => {
                const data = res.data
                const tasks = data.map(task =>
                    <div key={task.id} className="list-group-item d-flex justify-content-between">
                        <h6>{task.task}</h6>
                        <p>{task.body}</p>
                        <p input type={Date}>{task.timestamp.substring(0,10)}</p>
                        <p>{task.completed}</p>
                    </div>
                )

                this.setState({
                    tasks
                })
            })

            .catch((error) => {
                console.log(error)
            })
    };

    componentDidMount(){
        this.createTaskList()
    }

    render() {
        return (
        <div className="container">
            <h1 className="text-white text-uppercase text-center my-4">TASKS</h1>
            <div className="row">
            <div className="col-md-6 col-sm-10 mx-auto p-0">
                <div className="card p-3">
                <div className="mb-4">
                    <button className="btn btn-dark">
                    Add task
                    </button>
                </div>
                <ul className="list-group list-group-flush border-top-0">
                    {this.state.tasks}
                </ul>
                </div>
            </div>
            </div>
      </div>
        )
    }
}

export default Main;
